import './navigationBar.css'

import { Text } from '@consta/uikit/Text';
import { Button } from '@consta/uikit/Button';

import { useNavigate } from 'react-router-dom';

import { useSelector } from 'react-redux'

import { getUser, isAuth } from '../components/app/store'

import { useDispatch } from 'react-redux';
import { logout } from '../components/app/slices/authSlice';


export default function NavBar({ children }) {
    const user_data = useSelector(getUser)
    const is_logged_in = useSelector(isAuth)
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const goToHomePage = () => {
        navigate('/');
    };

    const goToServicePage = () => {
        navigate('/services');
    };

    const goToLoginPage = () => {
        navigate('/login');
    };
    if (!is_logged_in){
        return (
            <div className="header">
                <div className="header-left">
                    <Button label="Главная" onClick={goToHomePage} size="s" className="header-button" />
                    <Button label="Сервисы" onClick={goToServicePage} size="s" className="header-button" />
                </div>
                <div className="header-right">
                    <Text>
                        {user_data.firstName}
                        {user_data.lastName}
                    </Text>
                    <Button label="Войти" onClick={goToLoginPage} size="s" className="header-button" />
                </div>
            </div>
        );
    } else {
        <div className="header">
            <div className="header-left">
                <Button label="Главная" onClick={goToHomePage} size="s" className="header-button" />
                <Button label="Сервисы" onClick={goToServicePage} size="s" className="header-button" />
            </div>
            <div className="header-right">
                <Text>{user_data.first_name} {user_data.lastName}</Text>
                <Button label="Выйти" onClick={dispatch(logout())} size="s" className="header-button" />
            </div>
        </div>
    }
  }
