import LoginPage from '../pages/login-page/LoginPage';
import { user_login, user_logout } from '../components/actions/user'
import { connect } from 'react-redux'
import AuthRoute from '../components/app/AuthRoute'
const mapStateToProps = (state) => ({
    login: state.login
})

const mapDispatchToProps = (dispatch) => ({
    login_user: (is_logged_in) => dispatch(user_login(is_logged_in)),
    logout_user: (is_logged_in) => {
        localStorage.removeItem('accessToken')
        localStorage.removeItem('refreshToken')
        dispatch(user_logout(is_logged_in))
    }
})

export default connect(mapStateToProps, mapDispatchToProps)(LoginPage, AuthRoute)
